sys_script_include.script:
var NeedItUtils = Class.create();
var rightnow = new GlideDateTime();
NeedItUtils.prototype = {
	initialize: function() {
	},
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
	isDateToday: function(dateToCheck) {
		// Challenge:  Do not allow same-day requests
		// Get the date portion of rightnow and whenNeeded (no timestamp)
		var isToday = new GlideDateTime(dateToCheck);
		var dateCompare = GlideDateTime.subtract(rightnow, isToday);
		// Compare today and istoday to see if they are the same day
		if (rightnow.compareTo(isToday) == 0) {
			return true;
		}
		else {
			return false;
		}
	},
	type: 'NeedItUtils'
};
